<?php/** * A unique identifier is defined to store the options in the database and reference them from the theme. * By default it uses the theme name, in lowercase and without spaces, but this can be changed if needed. * If the identifier changes, it'll appear as if the options have been reset. */function optionsframework_option_name() {    // This gets the theme name from the stylesheet (lowercase and without spaces)    $themename = wp_get_theme();    $themename = $themename['Name'];    $themename = preg_replace("/\W/", "", strtolower($themename) );    $optionsframework_settings = get_option('optionsframework');    $optionsframework_settings['id'] = $themename;    update_option('optionsframework', $optionsframework_settings);}function optionsframework_options() {    // Slider Options    $slider_choice_array = array("none" => "No Showcase", "accordion" => "Accordion", "wpheader" => "WordPress Header", "image" => "Your Image", "easing" => "Easing Slider", "custom" => "Custom Slider");    // Pull all the categories into an array    $options_categories = array();    $options_categories_obj = get_categories();    foreach ($options_categories_obj as $category) {        $options_categories[$category->cat_ID] = $category->cat_name;    }    // Pull all the pages into an array    $options_pages = array();    $options_pages_obj = get_pages('sort_column=post_parent,menu_order');    $options_pages[''] = 'Select a page:';    foreach ($options_pages_obj as $page) {        $options_pages[$page->ID] = $page->post_title;    }    // If using image radio buttons, define a directory path    $radioimagepath =  get_stylesheet_directory_uri() . '/themeOptions/images/';    // define sample image directory path    $imagepath =  get_template_directory_uri() . '/images/demo/';    $options = array();    $options[] = array( "name" => __("General  Settings", 'addict'),                        "type" => "heading");	$options[] = array( "name" => __("General  Se